import {React, useState} from "react";
import "./navbar.css";
import { Link, useLocation } from "react-router-dom";
import CartSvg from "./cart.svg";


const NavBar = (props) =>{
    const currentQuntity = props
    const [quantity, setQuantity] = useState();
    const location = useLocation();
    const {quantityTest} = location.state;

    if(quantity !== currentQuntity.quantity){
        setQuantity(currentQuntity.quantity);
    }

    return(
        <div>
            <div className="navhome">
                <Link to={{
                    pathname:"/",
                    state:{
                        quantityTest: quantity}}} className="nav-link"><h1>Random Webshops Page</h1></Link>
                <div className="navbar">
                    <Link to={{
                    pathname:"/shop",
                    state:{
                        quantityTest: quantity}}} className="nav-link">Shop</Link>
                    <div className="cart-container"> 
                        <div className="quantity">{quantityTest}</div>
                        <Link to={{
                    pathname:"/cart",
                    state:{
                        quantityTest: quantity}}} className="nav-link"><img src={CartSvg} alt="cart"></img></Link>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default NavBar
