import {React, useState} from "react";
import "./navbar.css";
import { Link } from "react-router-dom";
import CartSvg from "./cart.svg";


const NavBar = (props) =>{
    const currentQuntity = props
    const [quantity, setQuantity] = useState();

    if(quantity !== currentQuntity.quantity){
        setQuantity(currentQuntity.quantity);
    }

    return(
        <div>
            <div className="navhome">
                <Link to={{
                    pathname:"/",
                    state:{
                    quantity: quantity}}} className="nav-link"><h1>Random Webshops Page</h1></Link>
                <div className="navbar">
                    <Link to="/shop" className="nav-link">Shop</Link>
                    <div className="cart-container"> 
                        <div className="quantity">{quantity}</div>
                        <Link to="/cart" className="nav-link"><img src={CartSvg} alt="cart"></img></Link>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default NavBar
